CREATE PROCEDURE usp_top10ProfitableProductsASBEGIN	DECLARE @pID varchar(10);	DECLARE @productName varchar(50);	DECLARE @qtySold int;	DECLARE @totalProfit float;	DECLARE @counter int = 0;	WHILE(@counter < 10)	BEGIN					SELECT @pID = productID, @qtySold = COUNT(*), @totalProfit = SUM(sellingPrice - costPrice)		FROM ProductItem		WHERE productItem.status = 'sold'		GROUP BY productID		PRINT (@pID + ' ' + CAST(@qtySold AS CHAR) + ' Total profit: ' + CAST(@totalProfit AS CHAR));		SET @counter = @counter + 1;	ENDEND;/*for items 1 to 10*/